import React, { useState, useEffect } from 'react';
import {
    Button, VStack, Text, useToast, Image, Flex, Heading, Switch, useDisclosure, Modal, ModalOverlay, ModalContent, ModalHeader, ModalCloseButton, ModalBody, FormControl, FormLabel, Input, Textarea, Select, ModalFooter,
} from '@chakra-ui/react';

import axios from 'axios';
import { useNavigate } from 'react-router-dom';  // Import useNavigate

const Profile = () => {
    const access = localStorage.getItem('access');
    const { isOpen, onOpen, onClose } = useDisclosure();
    const toast = useToast();
    const [detail, setDetail] = useState({
        id: '',
        first_name: '',
        last_name: '',
        user_type: '',
        address: '',
        profile_picture: '',
        email: '',
        username: '',
        password: '',
        repassword: '',
    });
    const [blogs, setBlogs] = useState([{
        title: '',
        image: 'hekko',
        category: 'hekko',
        summary: 'hekko',
        content: 'Mental Health',
        doctor: { first_name: '' },
        upload: false,
    }]);
    const [formData, setFormData] = useState({
        title: '', //input field
        image: null,  //image field
        category: '', //options are Mental Health, Heart Disease, Covid19, Immunization
        summary: '', //input field
        content: '', //textarea
        upload: false, //switch 
    });


    const navigate = useNavigate();


    const handleInputChange = (e) => {
        e.preventDefault()
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };
    const handleFileInputChange = (e) => {
        const { name } = e.target;
        const file = e.target.files[0];
        setFormData({
            ...formData,
            [name]: file,
        });
    };

    const handleSwitchChange = () => {
        setFormData((prevData) => ({ ...prevData, upload: !prevData.upload }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (detail.user_type === 'Patient') {
            toast({
                title: '',
                description: "Only Faculty can create Blogs",
                status: 'error',
                variant: 'left-accent',
                duration: 4000,
                isClosable: true,
                position: 'bottom'
            })
            return
        }
        try {
            console.log(formData);
            const response = await axios.post(`http://localhost:8000/api/createblog/?id=${localStorage.getItem('id')}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `JWT ${access}`,
                },
            });
            onClose();
            toast({
                title: '',
                description: response.data.message,
                status: 'success',
                variant: 'left-accent',
                duration: 4000,
                isClosable: true,
                position: 'bottom'
            })
        } catch (error) {
            console.log(error);
            toast({
                title: '',
                description: 'Failed to create new Blog',
                status: 'error',
                variant: 'left-accent',
                duration: 4000,
                isClosable: true,
                position: 'bottom'
            })
        }
    };
    const changeState = async (id, state) => {
        console.log(id, state);
        try {
            console.log(formData);
            const response = await axios.post(`http://localhost:8000/api/blogchange/?id=${id}&state=${state}`, {}, {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `JWT ${access}`,
                },
            });
            await fetchBlogs(detail.user_type);
            toast({
                title: '',
                description: response.data.message,
                status: 'success',
                variant: 'left-accent',
                duration: 4000,
                isClosable: true,
                position: 'bottom'
            })
        } catch (error) {
            console.log(error);
            toast({
                title: '',
                description: 'Failed to change the state ',
                status: 'error',
                variant: 'left-accent',
                duration: 4000,
                isClosable: true,
                position: 'bottom'
            })
        }
    };



    const handleLogout = async () => {
        try {
            toast({
                title: '',
                description: 'Logout Successfull',
                status: 'success',
                variant: 'left-accent',
                duration: 3000,
                isClosable: true,
                position: 'bottom'
            })
            localStorage.removeItem("access");
            localStorage.removeItem("refresh");
            localStorage.removeItem("id");
            navigate('/');
        } catch (error) {
            console.error(error);
        }
    };
    const fetchData = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/api/user/?id=${localStorage.getItem('id')}`, {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `JWT ${access}`,
                },
            });
            setDetail(response.data)
            console.log(response.data);
            await fetchBlogs(response.data.user_type);
        } catch (error) {
            handleLogout()
            console.error(error);
        }
    };
    const fetchBlogs = async (type) => {
        try {
            let response
            if (type === 'Patient') {
                response = await axios.get(`http://localhost:8000/api/blog/`, {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
            } else {
                response = await axios.get(`http://localhost:8000/api/blog/?id=${localStorage.getItem('id')}`, {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
            }
            setBlogs(response.data)
            console.log(response.data);
        } catch (error) {
            // handleLogout()
            console.error(error);
        }
    };
    useEffect(() => {
        if (access) {
            fetchData();

        }
        else handleLogout()
    }, []);
    return (
        <>
            <VStack as="form"
                spacing={4}
                align="stretch"
                maxW="400px"
                mx="auto"
                p={8}
                borderWidth={1}
                borderRadius="lg"
                boxShadow="lg">
                <Text fontSize="3xl" fontWeight="bold" textAlign={'center'}>
                    Profile Details
                </Text>
                {detail.profile_picture ? (
                    <Image
                        src={"http://localhost:8000" + detail.profile_picture}
                        alt="Profile Photo"
                        boxSize="150px"
                        objectFit="cover"
                        borderRadius="full"
                        my={4}
                        mx={'auto'}
                    />
                ) : (
                    <Text>No profile picture available</Text>
                )}
                <Text fontSize="lg" ml={'xl'}>
                    <strong>ID:</strong> {detail.id}
                </Text>
                <Text fontSize="lg" ml={'xl'}>
                    <strong>First Name:</strong> {detail.first_name}
                </Text>
                <Text fontSize="lg" ml={'xl'}>
                    <strong>Last Name:</strong> {detail.last_name}
                </Text>
                <Text fontSize="lg" ml={'xl'}>
                    <strong>Email:</strong> {detail.email}
                </Text>
                <Text fontSize="lg" ml={'xl'}>
                    <strong>Address:</strong> {detail.address}
                </Text>
                <Text fontSize="lg" ml={'xl'}>
                    <strong>Type:</strong> {detail.user_type}
                </Text>
                <Text fontSize="lg" ml={'xl'}>
                    <strong>Username:</strong> {detail.username}
                </Text>
                {/* Add more details as needed */}
                <Button colorScheme="teal" variant="solid" onClick={handleLogout}>
                    Logout
                </Button>
            </VStack>
            {detail.user_type === 'Doctor' ? (<div style={{ display: "flex", justifyContent: "center" }}>
                <Button colorScheme="teal" mt={'50px'} variant="solid" onClick={onOpen}>
                    Add Blog +
                </Button>
            </div >) : (<></>)}
            {blogs && blogs.length > 0 && (
                <VStack as="form"
                    // spacing={4}
                    alignItems={'start'}
                    maxW="full"
                    m="20px"
                    p={8}
                    borderWidth={1}
                    borderRadius="lg"
                    boxShadow="lg">
                    <Heading size='md'>
                        Blogs related Mental Health:
                    </Heading>
                    <Flex overflowX={'auto'}>
                        {
                            blogs && blogs.map((blog) => ((blog.category === 'Mental Health') ? (
                                <VStack
                                    as="form"
                                    spacing={4}
                                    align="stretch"
                                    maxW="400px"
                                    m="20px"
                                    borderWidth={1}
                                    borderRadius="lg"
                                    boxShadow="lg"
                                    key={blog.id} // Add a unique key for each mapped item
                                >
                                    {blog.image ? (
                                        <Image
                                            src={"http://localhost:8000" + blog.image}
                                            alt="Blog Image"
                                            boxSize="200px"
                                            width={'full'}
                                            objectFit="cover"
                                            mx={'auto'}
                                        />
                                    ) : (
                                        <Text>No image available</Text>
                                    )}
                                    <Text fontSize="lg" ml={'xl'} mx='15px'>
                                        <strong>{blog.title}</strong>
                                    </Text>
                                    {detail.user_type === 'Doctor' && (
                                        <Text fontSize="lg" ml={'xl'} mx='15px' textColor={(blog.upload) ? 'green' : 'red'}>
                                            <strong>{(blog.upload) ? '*Live' : 'Drafted'}</strong> <Switch size='sm' isChecked={blog.upload} onChange={() => changeState(blog.id, blog.upload)} />
                                        </Text>)}
                                    <Text fontSize="lg" ml={'xl'} mx='15px'>
                                        <strong>Category:</strong> {blog.category}
                                    </Text>
                                    <Text fontSize="lg" ml={'xl'} mx='15px'>
                                        <strong>Summary:</strong> {blog.summary}
                                    </Text>
                                    <Text fontSize="lg" ml={'xl'} mx='15px'>
                                        <strong>Created By:</strong> {blog.doctor.first_name}
                                    </Text>
                                    <Text fontSize="lg" ml={'xl'} mx='15px' mb='15px'>
                                        {blog.content}
                                    </Text>
                                </VStack>
                            ) : (<></>)))
                        }

                    </Flex >
                    <Heading size='md'>
                        Blogs related Heart Disease:
                    </Heading>
                    <Flex overflowX={'auto'}>
                        {blogs &&
                            blogs.map((blog) => ((blog.category === 'Heart Disease') ? (
                                <VStack
                                    as="form"
                                    spacing={4}
                                    align="stretch"
                                    maxW="400px"
                                    m="20px"
                                    borderWidth={1}
                                    borderRadius="lg"
                                    boxShadow="lg"
                                    key={blog.id} // Add a unique key for each mapped item
                                >
                                    {blog.image ? (
                                        <Image
                                            src={"http://localhost:8000" + blog.image}
                                            alt="Blog Image"
                                            boxSize="200px"
                                            width={'full'}
                                            objectFit="cover"
                                            mx={'auto'}
                                        />
                                    ) : (
                                        <Text>No image available</Text>
                                    )}
                                    <Text fontSize="lg" ml={'xl'} mx='15px'>
                                        <strong>{blog.title}</strong>
                                    </Text>
                                    {detail.user_type === 'Doctor' && (
                                        <Text fontSize="lg" ml={'xl'} mx='15px' textColor={(blog.upload) ? 'green' : 'red'}>
                                            <strong>{(blog.upload) ? '*Live' : 'Drafted'}</strong> <Switch size='sm' isChecked={blog.upload} onChange={() => changeState(blog.id, blog.upload)} />
                                        </Text>)}
                                    <Text fontSize="lg" ml={'xl'} mx='15px'>
                                        <strong>Category:</strong> {blog.category}
                                    </Text>
                                    <Text fontSize="lg" ml={'xl'} mx='15px'>
                                        <strong>Summary:</strong> {blog.summary}
                                    </Text>
                                    <Text fontSize="lg" ml={'xl'} mx='15px'>
                                        <strong>Created By:</strong> {blog.doctor.first_name}
                                    </Text>

                                    <Text fontSize="lg" ml={'xl'} mx='15px' mb='15px'>
                                        {blog.content}
                                    </Text>
